mapper.select:
select merbercardid as "merbercardid",memberid as "memberid",merchantid as "merchantid",cardno as "cardno",dzcardno as "dzcardno",
cardgrade as "cardgrade",cardcode as "cardcode",cardstat as "cardstat",cardmode as "cardmode",bz as "bz" from membercard
where merbercardid = #{merbercardid}
