mapper.select:
select a.consumesj as "consumesj", c.merchantaddr as "merchantaddr", a.consumenum as "consumenum",
	b.cardname as "cardname", b.dzcardno as "dzcardno", c.merbercardimg as "merbercardimg", a.consumenr as "consumenr"
	from consume a, membercard b, merchant c, member d
	where a.membercardid = b.merbercardid 
	and b.merchantid = c.merchantid
	and a.memberid = d.memberid
	and d.memberopenid = #{bean.openid} 
	and c.urlflag = #{bean.urlflag}
	and a.dzcardno = #{bean.dzcardno}
